import React from 'react';
import {connect} from 'react-redux';
import {fetchplan} from '../../actions/index';
const axios = require('axios');
class PlanShow extends React.Component{
   state= {
       inputfoodvalue:null
   };
    componentDidMount() {
       this.props.fetchplan(this.props.match.params.id)
   }
   renderFOODinfo()
   {
   return(    
   <form className="ui form">
    <div className="field">
      <label>Search calorie detail by food name</label>
      <input type="text" id="inputfoodvalue" name="Food-Name" placeholder="FOOD Name"/>
    </div>

    <button className="ui button" type="submit" onClick={this.foodValue} onSubmit={(e)=>{e.preventDefault()}}>Submit</button>
  </form>);

   }
   renderDetails({Age,Weight,Height,Name,Sex,Plan,waist,hip}) 
   {
       return (<div className="ui segments">
       <div className="ui segment">
         <p>Plan:{Plan}</p>
       </div>
       <div className="ui red segment">
         <p>Name:{Name}</p>
       </div>
       <div className="ui blue segment">
         <p>Age(in yrs):{Age}</p>
       </div>
       <div className="ui green segment">
         <p>Weight(in KG):{Weight}</p>
       </div>
       <div className="ui yellow segment">
         <p> Height(in cm) :{Height}</p>
       </div>
       <div className="ui yellow segment">
         <p> Waist(in cm): {waist}</p>
       </div>
       <div className="ui yellow segment">
         <p>Hip(in cm):{hip}</p>
       </div>   
       <div className="ui yellow segment">
        <p>Sex(in cm): {Sex}</p>
       </div>
     </div>)
   }
    render() {
        if(!this.props.plan)
        {
            return <div>Loading</div>
        }
    return (
    <div> 
        {this.renderDetails(this.props.plan)}
        {this.renderFOODinfo()}
    </div>);
}
}
const mapStateToProps = (state,ownProps) =>{
return {
    plan:state.plans[ownProps.match.params.id]
}
}
export default connect(mapStateToProps,{fetchplan})(PlanShow);
