import React from 'react';
import {connect} from 'react-redux';
import {fetchplan} from '../../actions/index';
//const axios = require('axios');

class PlanShow extends React.Component{
   state= {
       inputfoodvalue:null
   };
    componentDidMount() {
       this.props.fetchplan(this.props.match.params.id)
   }
   renderInput = ({input,label,meta})=>{
    const className=`field ${meta.error&&meta.touched ? 'error' : ''}`
     return(
     <div className={className}>
         <label>{label}</label>
         <input {...input} autoComplete="off"/>
         {this.renderError(meta)}
     </div>);
 }
   renderFOODinfo()
   {
   return(    
   <form className="ui form">
        <Field name="hip"  component={this.renderInput} label="Enter Food Name"/>
  </form>);

   }
   renderDetails({Age,Weight,Height,Name,Sex,Plan,waist,hip}) 
   {
       return (<div className="ui segments">
       <div className="ui segment">
         <p>Plan:{Plan}</p>
       </div>
       <div className="ui red segment">
         <p>Name:{Name}</p>
       </div>
       <div className="ui blue segment">
         <p>Age(in yrs):{Age}</p>
       </div>
       <div className="ui green segment">
         <p>Weight(in KG):{Weight}</p>
       </div>
       <div className="ui yellow segment">
         <p> Height(in cm) :{Height}</p>
       </div>
       <div className="ui yellow segment">
         <p> Waist(in cm): {waist}</p>
       </div>
       <div className="ui yellow segment">
         <p>Hip(in cm):{hip}</p>
       </div>   
       <div className="ui yellow segment">
        <p>Sex(in cm): {Sex}</p>
       </div>
     </div>)
   }
    render() {
        if(!this.props.plan)
        {
            return <div>Loading</div>
        }
    return (
    <div> 
        {this.renderDetails(this.props.plan)}
        {this.renderFOODinfo()}
    </div>);
}
}
const mapStateToProps = (state,ownProps) =>{
return {
    plan:state.plans[ownProps.match.params.id]
}
}
export default connect(mapStateToProps,{fetchplan})(PlanShow);
