import React from 'react';
import {connect} from 'react-redux';
import {fetchplan} from '../../actions/index';
import Modal from '../../components/Modal';
const axios = require('axios');
class PlanShow extends React.Component{
   state= {
       inputfoodvalue:null,
        calcium: 0,
        carbohydrt: 0,
        cholestrl: 0, 
        energ_kcal: 0,
        iron: 0,
        magnesium:0, 
        manganese: 0,
        phosphorus: 0, 
        potassium: 0,
        protein: 0,
        sodium: 0, 
        vit_b6:0,
        vit_b12: 0, vit_c: 16.7,vit_d: 0, vit_e: 0.6, vit_k: 0,vit_a_iu:294, water: 84
   };
    componentDidMount() {
       this.props.fetchplan(this.props.match.params.id)

   }
   renderErrors(error) {
     alert('Error ); try again')
   }
   getFoodDetails(value) {
    axios({
        "method":"GET",
        "url":"https://food-calorie-data-search.p.rapidapi.com/api/search",
        "headers":{
        "content-type":"application/octet-stream",
        "x-rapidapi-host":"food-calorie-data-search.p.rapidapi.com",
        "x-rapidapi-key":"4fce675d58msh4c5b8d7782ff444p1f819fjsn503f83c9b052"
        },"params":{
        "keyword":`${value}`
        }
        })
        .then((response)=>{
          console.log(response.data[0])
        })
        .catch((error)=>{
          {this.renderErrors(error)}
        })

   }

   renderDetails({Age,Weight,Height,Name,Sex,Plan,waist,hip}) 
   {
       return (
       <div className="ui segments">
       <div className="ui segment">
         <p>Plan:{Plan}</p>
       </div>
       <div className="ui red segment">
         <p>Name:{Name}</p>
       </div>
       <div className="ui blue segment">
         <p>Age(in yrs):{Age}</p>
       </div>
       <div className="ui green segment">
         <p>Weight(in KG):{Weight}</p>
       </div>
       <div className="ui yellow segment">
         <p> Height(in cm) :{Height}</p>
       </div>
       <div className="ui yellow segment">
         <p> Waist(in cm): {waist}</p>
       </div>
       <div className="ui yellow segment">
         <p>Hip(in cm):{hip}</p>
       </div>   
       <div className="ui yellow segment">
        <p>Sex(in cm): {Sex}</p>
       </div>
     </div>)
   }
    render() {
        if(!this.props.plan)
        {
            return <div>Loading</div>
        }
    return (
    <div> 
        {this.renderDetails(this.props.plan)}
        <input type="text" id="input"></input>
        <button 
        onClick={()=>{
            this.setState({
                inputfoodvalue:document.getElementById('input').value
            })
        }}>
            Submit
        </button>
        {this.getFoodDetails(this.state.inputfoodvalue)}
    </div>);
}
}
const mapStateToProps = (state,ownProps) =>{
return {
    plan:state.plans[ownProps.match.params.id]
}
}
export default connect(mapStateToProps,{fetchplan})(PlanShow);
















