import React from 'react';
import {connect} from 'react-redux';
import {fetchplan} from '../../actions/index';
const axios = re
class PlanShow extends React.Component{
   componentDidMount() {
       this.props.fetchplan(this.props.match.params.id)
   }
   renderBMIinfo({Age,Weight,Height,Name,Sex,Plan,waist,hip})
   {

axios({
    "method":"POST",
    "url":"https://bmi.p.rapidapi.com/",
    "headers":{
    "content-type":"application/json",
    "x-rapidapi-host":"bmi.p.rapidapi.com",
    "x-rapidapi-key":"4fce675d58msh4c5b8d7782ff444p1f819fjsn503f83c9b052",
    "accept":"application/json"
    },"data":{
    "weight":{
    "value":"85.00",
    "unit":"kg"
    },"height":{
    "value":"170.00",
    "unit":"cm"
    },"sex":"m",
    "age":"24",
    "waist":"34.00",
    "hip":"40.00"
    }
    })
    .then((response)=>{
      console.log(response)
    })
    .catch((error)=>{
      console.log(error)
    })
   }
   renderDetails({Age,Weight,Height,Name,Sex,Plan,waist,hip}) 
   {
       return (<div className="ui segments">
       <div className="ui segment">
         <p>Plan:{Plan}</p>
       </div>
       <div className="ui red segment">
         <p>Name:{Name}</p>
       </div>
       <div className="ui blue segment">
         <p>Age(in yrs):{Age}</p>
       </div>
       <div className="ui green segment">
         <p>Weight(in KG):{Weight}</p>
       </div>
       <div className="ui yellow segment">
         <p> Height(in cm) :{Height}</p>
       </div>
       <div className="ui yellow segment">
         <p> Waist(in cm): {waist}</p>
       </div>
       <div className="ui yellow segment">
         <p>Hip(in cm):{hip}</p>
       </div>   
       <div className="ui yellow segment">
        <p>Sex(in cm): {Sex}</p>
       </div>
     </div>)
   }
    render() {
        if(!this.props.plan)
        {
            return <div>Loading</div>
        }
    return (
    <div> 
        {this.renderDetails(this.props.plan)}
        {this.renderBMIinfo(this.props.plan)}
    </div>);
}
}
const mapStateToProps = (state,ownProps) =>{
return {
    plan:state.plans[ownProps.match.params.id]
}
}
export default connect(mapStateToProps,{fetchplan})(PlanShow);
