import React from 'react';
import {connect} from 'react-redux';
import {signIn,signOut} from '../actions';

class GoogleAuth extends React.Component{
    componentDidMount(){
        window.gapi.load('client:auth2',()=>{
            window.gapi.client.init({
                clientId:'612311695694-tqh57d47b7bh63o45tcn1rv32m5te5fg.apps.googleusercontent.com',
                scope:'email'
            }).then(()=>{
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn);
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }
    onAuthChange = (isSignedIn) => {
        if(isSignedIn){
            this.props.signIn();
                }
        else {
            this.props.signOut();
        }
    }
    onSignIn = () =>{
        this.auth.signIn();
    };
    onSignOut = () =>{
        this.auth.signOut();
    };
    renderAuthButton(){
        if(this.props.isSignedIn===null)
        {
            return null;
        }
        else if(this.props.isSignedIn===true)
        {
            return (<button onClick={this.onSignOut} className="ui red google button">
                <i className="google icon"/>
                Sign Out
            </button>);
        } 
        else {
           return(<button onClick={this.onSignIn} className="ui red google button">
            <i className="google icon"/>
            Sign In
        </button>);
        }
    }
    render() {
        return (<div>{this.renderAuthButton()}</div>);
    }
}
const mapStateToProps = (state) =>{
return {
    isSignedIn:state.auth.isSignedIn
};
}
export default connect(mapStateToProps,{signIn,signOut})(GoogleAuth);
