import React from "react";
import SearchBar from "./SearchBar";
import VideoList from "./VideoList";
import VideoDetails from "./VideoDetails";
import { connect } from "react-redux";
import { fetchplan } from "../../actions/index";
const bodyMassIndex = require("body-mass-index");
const axios = require("axios");
class PlanShow extends React.Component {
	state = {
		inputfoodvalue: null,
		calcium: 0,
		carbohydrt: 0,
		cholestrl: 0,
		energ_kcal: 0,
		iron: 0,
		magnesium: 0,
		manganese: 0,
		phosphorus: 0,
		potassium: 0,
		protein: 0,
		sodium: 0,
		vit_b6: 0,
		vit_b12: 0,
		vit_c: 0,
		vit_d: 0,
		vit_e: 0,
		vit_k: 0,
		vit_a_iu: 0,
		water: 0,
		bmi: 0,
		videos: [],
		selectedVideo: null,
		Age:0,
		Weight:0,
		Height:0,
		Name:0,
		Sex:0,
		Plan:0,
		waist:0,
		hip:0,
		typeOfPerson:0,
		diseaseRisk:0
	};
	componentDidMount() {
		this.props.fetchplan(this.props.match.params.id);
		this.onTermSubmit("Benefits of exercise regular");

	}
	componentDidUpdate() {}
	renderErrors() {
		console.log("Null value or click again");
	}
	getFoodDetails() {
		axios({
			method: "GET",
			url: "https://food-calorie-data-search.p.rapidapi.com/api/search",
			headers: {
				"content-type": "application/octet-stream",
				"x-rapidapi-host": "food-calorie-data-search.p.rapidapi.com",
